import React from "react";

const hashtags = data => {
  console.log("data =>", data);
  if (data.entities.hashtags) {
    console.log(data.entities.hashtags.map(hashtag => hashtag.tag).join(" #"));
    // return <p>data.entities.hashtags.map(hashtag => hashtag.tag).join(" #")</p>;
  }
};

const Tweet = ({ data }) => {
  console.log(data);
  return (
    <div className="card">
      <div className="content">
        <div className="header">Hashtags</div>
        <div className="meta">Some company</div>
        <div className="description">{data.text}</div>
      </div>
    </div>
  );
};

export default Tweet;
