import React from "react";

const hashtags = data => {
  if (
    data.extended_tweet &&
    data.extended_tweet.entities &&
    data.extended_tweet.entities.hashtags
  ) {
    return data.extended_tweet.entities.hashtags.map(hashtag => (
      <span key={data.id + hashtag.text} className="ui label">
        #{hashtag.text}
      </span>
    ));
  }
};

const annotations = data => {
  console.log("here");
  if (
    data.extended_tweet &&
    data.extended_tweet.entities & data.extended_tweet.entities.annotations
  ) {
    return data.entities.annotations.context.map(context => (
      <span className="ui blue label">{context.context_entity_name}</span>
    ));
  }
};

const title = data => {
  if (
    data.extended_tweet &&
    data.extended_tweet.entities &&
    data.extended_tweet.entities.urls[0] &&
    data.extended_tweet.entities.urls[0].unwound
  ) {
    return data.extended_tweet.entities.urls[0].unwound.title;
  }
};

const full_text = data => {
  if (data.extended_tweet && data.extended_tweet.full_text) {
    return data.extended_tweet.full_text;
  } else {
    return data.text;
  }
};

const ExtendedTweet = ({ data }) => {
  return (
    <div className="ui segment">
      <h4 className="ui header">{title(data)}</h4>
      <p>{full_text(data)}</p>
      {hashtags(data)}
      {annotations(data)}
    </div>
  );
};

export default ExtendedTweet;
