import React, { useEffect, useReducer } from "react";
import request from "request";
import util from "util";

import Tweet from "./Tweet";

const reducer = (state, action) => {
  switch (action.type) {
    case "add_tweet":
      return [...state, action.payload];
    default:
      return state;
  }
};

const AnnotationTweets = () => {
  // const [state, setTweets] = useState([]);
  const [state, dispatch] = useReducer(reducer, []);
  const tweets = state;

  const fetchTweets = () => {
    const get = util.promisify(request.get);
    const stream = request.get("http://localhost:3000/api/stream/filter");

    stream
      .on("data", data => {
        try {
          console.log(JSON.parse(data));
          data = JSON.parse(data);
          dispatch({ type: "add_tweet", payload: data.data });
          // setTweets([...tweets, data.data]);
          // console.log("tweets =>", tweets);
        } catch (e) {}
      })
      .on("error", error => {
        if ((error.code = "ETIMEDOUT")) {
          console.log("TIMEOUT");
        }
      });
  };

  useEffect(() => {
    fetchTweets();
  }, []);

  return (
    <div className="ui container">
      {tweets.map(tweet => (
        <Tweet key={tweet.id} data={tweet} />
      ))}
    </div>
  );
};

export default AnnotationTweets;
