import React from "react";

const hashtags = data => {
  console.log("data =>", data);
  if (data.entities.hashtags) {
    // console.log(data.entities.hashtags.map(hashtag => hashtag.tag).join(" #"));
    // data.entities.hashtags.map(hashtag => hashtag.tag);
    // return <p>data.entities.hashtags.map(hashtag => hashtag.tag).join(" #")</p>;
    return data.entities.hashtags.map(hashtag => (
      <span className="ui label">hashtag.tag</span>
    ));
  }
};

const title = data => {
  if (data.entities && data.entities.urls) {
    return data.entities.urls[0].title;
  }
};

const Tweet = ({ data }) => {
  return (
    <div className="ui segment">
      <h4 className="ui header">{title(data)}</h4>
      <p>{data.text}</p>
      {hashtags(data)}
    </div>
  );
};

export default Tweet;
