import React from "react";

const hashtags = data => {
  if (data.entities && data.entities.hashtags) {
    return data.entities.hashtags.map(hashtag => (
      <span key={hashtag.tag} className="ui label">
        #{hashtag.tag}
      </span>
    ));
  }
};

const title = data => {
  if (data.entities && data.entities.urls) {
    return data.entities.urls[0].title;
  }
};

const full_text = data => {
  if (data.extended_tweet && data.extended_tweet.full_text) {
    return data.extended_tweet.full_text;
  } else {
    return data.text;
  }
};

const Tweet = ({ data }) => {
  return (
    <div className="ui segment">
      <h4 className="ui header"></h4>
      <p>full_text(data)</p>
    </div>
  );
};

export default Tweet;
