import React from "react";

const hashtags = data => {
  console.log("data =>", data);
  if (data.entities.hashtags) {
    console.log(data.entities.hashtags.map(hashtag => hashtag.tag).join(" #"));
    // return <p>data.entities.hashtags.map(hashtag => hashtag.tag).join(" #")</p>;
  }
};

const title = data => {
  if (data.entities && data.entities.urls) {
    return data.entities.urls[0].title;
  }
};

const Tweet = ({ data }) => {
  console.log(data);
  return (
    <div class="ui segment">
      <h4 class="ui header">{title()}</h4>
      <p>{data.text}</p>
    </div>
  );
};

export default Tweet;
